(()=>{"use strict";document.getElementById("sendMessageButton").addEventListener("click",(e=>{e.preventDefault();const t=document.querySelector('input[name="option"]:checked');if(t){const e=t.value;chrome.tabs.query({active:!0,currentWindow:!0},(t=>{chrome.tabs.sendMessage(t[0].id,{action:"selectedOption",value:e},(e=>{e&&console.log(e.status)}))}))}}))})();